import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { catchError, of } from 'rxjs';

@Component({
  selector: 'app-seller-auth',
  templateUrl: './seller-auth.component.html',
  styleUrls: ['./seller-auth.component.css']
})
export class SellerAuthComponent implements OnInit{

  constructor(private http: HttpClient) {}

  ngOnInit(): void {

  }

  signUp(data:object): void{
    this.http.post('http://localhost:8080/utente/registrati',data).pipe(
      catchError((error) => {
        // Gestisci l'errore
        console.error('Errore durante la registrazione:', error);
        // Esegui eventuali azioni aggiuntive in caso di errore
        // Puoi anche restituire un valore di fallback o lanciare un'eccezione
        return of(null); // Esempio: restituisci un valore di fallback (null)
      })
    )
    .subscribe((response) => {
      if (response) {
        // Gestisci la risposta in caso di successo
        console.log('Registrazione avvenuta con successo:', response);
        // Esegui eventuali azioni aggiuntive dopo la registrazione
      }
    });
  }
}
